body.script:
// Current Year
document.getElementById('current-year').textContent = new Date().getFullYear();

// Mobile Menu Toggle
const navToggle = document.getElementById('nav-toggle');
const navMenu = document.getElementById('nav-menu');

if (navToggle && navMenu) {
navToggle.addEventListener('click', function() {
navMenu.classList.toggle('active');
});
}

// Breaking News Ticker
function initBreakingNews() {
fetch('/feeds/posts/default?alt=json&max-results=10&orderby=published')
.then(response => response.json())
.then(data => {
const entries = data.feed.entry || [];
const ticker = document.getElementById('breaking-ticker');

if (entries.length > 0 && ticker) {
ticker.innerHTML = '';
entries.forEach((entry, index) => {
const title = entry.title.$t;
const link = entry.link.find(l => l.rel === 'alternate').href;
const item = document.createElement('a');
item.href = link;
item.className = 'ticker-item';
item.textContent = title;
item.style.animationDelay = `${index * 4}s`;
ticker.appendChild(item);
});
}
})
.catch(error => console.error('Error fetching breaking news:', error));
}

// Theme Toggle
const themeToggle = document.getElementById('theme-toggle');
const savedTheme = localStorage.getItem('theme');
const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' :
'light';
const currentTheme = savedTheme || systemTheme;
document.documentElement.setAttribute('data-theme', currentTheme);

if (themeToggle) {
themeToggle.addEventListener('click', function() {
const theme = document.documentElement.getAttribute('data-theme') === 'light' ? 'dark' :
'light';
document.documentElement.setAttribute('data-theme', theme);
localStorage.setItem('theme', theme);
themeToggle.textContent = theme === 'dark' ? '☀️' : '🌙';
});

themeToggle.textContent = currentTheme === 'dark' ? '☀️' : '🌙';
}

// Back to Top
const backToTop = document.getElementById('back-to-top');
let scrollTimeout;

if (backToTop) {
window.addEventListener('scroll', () => {
if (scrollTimeout) {
window.cancelAnimationFrame(scrollTimeout);
}

scrollTimeout = window.requestAnimationFrame(function() {
if (window.scrollY > 300) {
backToTop.classList.add('show');
} else {
backToTop.classList.remove('show');
}
});
});

backToTop.addEventListener('click', function() {
window.scrollTo({ top: 0, behavior: 'smooth' });
});
}

// Auto Paragraph Fixer (Transmutation Script)
function fixParagraphs() {
const postBody = document.querySelector('.post-body');
if (postBody && postBody.innerHTML.includes('<br')) {
let content = postBody.innerHTML;

// Replace multiple br tags with paragraph breaks
content = content.replace(/(<br\s*\/?>){2,}/gi, '</p><p>');

// Wrap content that starts without a paragraph tag
if (!content.trim().startsWith('<p')) {
content = '<p>' + content;
}

// Ensure content ends with closing paragraph
if (!content.trim().endsWith('</p>')) {
content = content + '</p>';
}

// Clean up empty paragraphs
content = content.replace(/<p>\s*<\/p>/g, '');

postBody.innerHTML = content;
}
}

// Auto Table of Contents Generator
function generateTOC() {
const tocContainer = document.getElementById('auto-toc');
const postBody = document.querySelector('.post-body');

if (tocContainer && postBody) {
const headings = postBody.querySelectorAll('h2, h3');

if (headings.length > 3) {
const tocHTML = ['<h3 class="toc-title">Table of
Contents</h3><ul class="toc-list">'];
headings.forEach(function(heading, index) {
const id = 'toc-' + index;
heading.id = id;

const level = heading.tagName.toLowerCase();
const className = level === 'h3' ? 'toc-h3' : '';

tocHTML.push('<li class="' + className + '"><a href="#' + id +
'">' + heading.textContent + '</a></li>');
});

tocHTML.push('</ul>');
tocContainer.innerHTML = tocHTML.join('');
tocContainer.style.display = 'block';
}
}
}

// Copy Link Function
function copyPostLink(event) {
event.preventDefault();
const url = window.location.href;

if (navigator.clipboard) {
navigator.clipboard.writeText(url).then(function() {
const button = event.currentTarget;
const originalText = button.textContent;
button.textContent = '✓ Copied!';
setTimeout(function() {
button.textContent = originalText;
}, 2000);
});
}
}

// Related Posts Fetcher
function fetchRelatedPosts() {
const container = document.getElementById('related-posts-container');
if (container && window.location.pathname !== '/') {
// Get current page category from breadcrumb or other source
const categoryLink = document.querySelector('.breadcrumbs
a[href*="/search/label/"]');
if (categoryLink) {
const categoryName = categoryLink.textContent;

fetch('/feeds/posts/default/-/' + encodeURIComponent(categoryName) +
'?alt=json&max-results=6')
.then(function(response) { return response.json(); })
.then(function(data) {
const entries = data.feed.entry || [];
const currentUrl = window.location.href;
const relatedPosts = entries
.filter(function(entry) { return entry.link.find(function(l) { return l.rel ===
'alternate'; }).href !== currentUrl; })
.slice(0, 4);

if (relatedPosts.length > 0) {
const html = relatedPosts.map(function(post) {
const title = post.title.$t;
const link = post.link.find(function(l) { return l.rel === 'alternate'; }).href;
const thumbnail = post.media$thumbnail ? post.media$thumbnail.url : '';

return '<div class="post-card">' +
(thumbnail ? '<img class="post-thumbnail" src="' + thumbnail + '"
alt="' + title + '" loading="lazy">' : '<div
class="post-thumbnail">📄</div>') +
'<div class="post-content"><h4><a href="' + link +
'">' + title + '</a></h4></div></div>';
}).join('');

container.innerHTML = html;
}
})
.catch(function(error) { console.error('Error fetching related posts:', error); });
}
}
}

// Sentient Schema Engine - Auto-detect content patterns
function generateDynamicSchema() {
const postBody = document.querySelector('.post-body');
if (!postBody) return;

const schemas = [];

// Detect FAQ pattern
const potentialFAQs = [];
const headings = postBody.querySelectorAll('h3');

headings.forEach(function(heading) {
const nextElement = heading.nextElementSibling;
if (nextElement && nextElement.tagName === 'P') {
potentialFAQs.push({
question: heading.textContent,
answer: nextElement.textContent
});
}
});

if (potentialFAQs.length >= 3) {
schemas.push({
"@context": "https://schema.org",
"@type": "FAQPage",
"mainEntity": potentialFAQs.map(function(faq) {
return {
"@type": "Question",
"name": faq.question,
"acceptedAnswer": {
"@type": "Answer",
"text": faq.answer
}
};
})
});
}

// Insert generated schemas
schemas.forEach(function(schema) {
const script = document.createElement('script');
script.type = 'application/ld+json';
script.textContent = JSON.stringify(schema);
document.head.appendChild(script);
});
}

// Initialize everything on DOM ready
document.addEventListener('DOMContentLoaded', function() {
// Initialize breaking news
initBreakingNews();

// For single posts
if (document.body.classList.contains('item')) {
fixParagraphs();
generateTOC();
fetchRelatedPosts();
generateDynamicSchema();
}

// For all pages
if (document.body.classList.contains('index')) {
fixParagraphs();
}
});

// Make functions globally available
window.copyPostLink = copyPostLink;
